CREATE OR REPLACE FUNCTION obter_diluicao_medic_adep (nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w	bigint;
nr_seq_item_w		bigint;
ds_diluicao_w		varchar(2000);


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	/* obter dados horario prescricao */

	select	nr_prescricao,
		nr_seq_material
	into STRICT	nr_prescricao_w,
		nr_seq_item_w
	from	prescr_mat_hor
	where	nr_sequencia = nr_seq_horario_p
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	/* obter diluicao */

	select	obter_diluicao_medic(nr_seq_item_w, nr_prescricao_w)
	into STRICT	ds_diluicao_w
	;
end if;

return substr(ds_diluicao_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
