CREATE OR REPLACE FUNCTION obter_diretorio_imagem_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_diretorio_w			varchar(2000);
ds_arquivo_imagem_w		varchar(2000);

c01 CURSOR FOR
SELECT	a.ds_arquivo_imagem
from	laudo_paciente_imagem a
where	a.nr_sequencia = nr_seq_laudo_p
and	(a.ds_arquivo_imagem IS NOT NULL AND a.ds_arquivo_imagem::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	ds_arquivo_imagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(length(ds_diretorio_w),0) = 0) then

		ds_diretorio_w := 	ds_arquivo_imagem_w;
	else

		ds_diretorio_w := 	ds_diretorio_w || ';' || ds_arquivo_imagem_w;

	end if;

	end;
end loop;
close C01;


return	ds_diretorio_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
