SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dispensacao_farmacia ( nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_regra_item_w		varchar(1);
ie_regra_dil_w		varchar(1);
ie_regra_kit_w		varchar(1);
ie_regra_recons		varchar(1);
ds_retorno_w		varchar(255);
nr_sequencia_w		bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	prescr_material
	where	nr_prescricao 		= nr_prescricao_p
	and	nr_seq_substituto	= nr_seq_item_p;


	/* atualizar dispensação item */

	select	max(ie_regra_disp)
	into STRICT	ie_regra_item_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= coalesce(nr_sequencia_w,nr_seq_item_p);

	/* atualizar dispensação diluentes */

	SELECT	MAX(ie_regra_disp)
	INTO STRICT	ie_regra_dil_w
	FROM	prescr_material
	WHERE	nr_prescricao		= nr_prescricao_p
	and	ie_agrupador		= 3
	AND	nr_sequencia_diluicao	= coalesce(nr_sequencia_w,nr_seq_item_p);

	/* atualizar dispensação reconstituição */

	SELECT	MAX(ie_regra_disp)
	INTO STRICT	ie_regra_recons
	FROM	prescr_material
	WHERE	nr_prescricao		= nr_prescricao_p
	and	ie_agrupador		= 9
	AND	nr_sequencia_diluicao	= coalesce(nr_sequencia_w,nr_seq_item_p);

	/* atualizar dispensação dos itens que foram gerados via kit */

	select	max(ie_regra_disp)
	into STRICT	ie_regra_kit_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_kit		= coalesce(nr_sequencia_w,nr_seq_item_p);
end if;

if (ie_regra_item_w IS NOT NULL AND ie_regra_item_w::text <> '') then
	ds_retorno_w := 'M: '|| ie_regra_item_w;
end if;
if (ie_regra_dil_w IS NOT NULL AND ie_regra_dil_w::text <> '') then
	ds_retorno_w := ds_retorno_w || ', R: ' || ie_regra_recons;
	ds_retorno_w := ds_retorno_w || ', D: ' || ie_regra_dil_w;
end if;
if (ie_regra_kit_w IS NOT NULL AND ie_regra_kit_w::text <> '') then
	ds_retorno_w := ds_retorno_w || ', K: ' || ie_regra_kit_w;
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dispensacao_farmacia ( nr_prescricao_p bigint, nr_seq_item_p bigint) FROM PUBLIC;

