CREATE OR REPLACE FUNCTION obter_dispositivo_atual_gas ( nr_seq_gasoterapia_p bigint, nr_seq_horario_p bigint default null) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_dispositivo_w	varchar(15);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	prescr_gasoterapia_evento
where	((nr_seq_gasoterapia	in (	SELECT	b.nr_seq_gasoterapia
										from	prescr_gasoterapia_hor b
										where	b.nr_sequencia = nr_seq_horario_p)) or (nr_seq_gasoterapia	= nr_seq_gasoterapia_p))
and		(ie_disp_resp_esp IS NOT NULL AND ie_disp_resp_esp::text <> '');

if (nr_sequencia_w	= 0) then
	select	max(ie_disp_resp_esp)
	into STRICT	ie_dispositivo_w
	from	prescr_gasoterapia
	where	((nr_sequencia	in (	SELECT	b.nr_seq_gasoterapia
										from	prescr_gasoterapia_hor b
										where	b.nr_sequencia = nr_seq_horario_p)) or (nr_sequencia	= nr_seq_gasoterapia_p));

else
	select	max(ie_disp_resp_esp)
	into STRICT	ie_dispositivo_w
	from	prescr_gasoterapia_evento
	where	((nr_seq_gasoterapia	in (	SELECT	b.nr_seq_gasoterapia
										from	prescr_gasoterapia_hor b
										where	b.nr_sequencia = nr_seq_horario_p)) or (nr_seq_gasoterapia	= nr_seq_gasoterapia_p))
	and		nr_sequencia			= nr_sequencia_w;
end if;

return	ie_dispositivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
