CREATE OR REPLACE FUNCTION obter_dispositivo_sitio ( cd_sitio_especifico_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15);


BEGIN

if (cd_sitio_especifico_p in ('LCBI','CSEP')) then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308939); -- CVC
elsif (cd_sitio_especifico_p in ('PNEU')) then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308940); -- Resp
elsif (cd_sitio_especifico_p in ('SUTI','ASB')) then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308941); -- SVD
end if;

return ds_retorno_w;

end;
$body$
