SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_disp_equip_agenda (nr_seq_agenda_p bigint, cd_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_disponivel_w		varchar(01)	:= 'S';
dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_equipamento_w		bigint;
qt_equip_agenda_w	bigint;
cd_classificacao_w		bigint;
qt_tempo_esterilizacao_w	bigint	:= 0;


BEGIN

select	coalesce(sum(qt_equipamento), 1),
	coalesce(max(cd_classificacao),0)
into STRICT	qt_equipamento_w,
	cd_classificacao_w
from	equipamento
where	cd_equipamento	= cd_equipamento_p;

if (cd_classificacao_w <> 0) then
	select	coalesce(max(qt_tempo_esterelizacao),0)
	into STRICT	qt_tempo_esterilizacao_w
	from	classif_equipamento
	where	nr_sequencia = cd_classificacao_w;
end if;

select	hr_inicio,
	hr_inicio + ((nr_minuto_duracao + coalesce(qt_tempo_esterilizacao_w,0)) / 1440)
into STRICT	dt_inicial_w,
	dt_final_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;

select	count(*)
into STRICT	qt_equip_agenda_w
from	agenda_pac_equip a,
	agenda_paciente b
where	b.nr_sequencia	= a.nr_seq_agenda
and	a.nr_sequencia	<> nr_seq_agenda_p
and	b.hr_inicio between dt_inicial_w and dt_final_w
and	(a.dt_confirmacao IS NOT NULL AND a.dt_confirmacao::text <> '')
and	a.cd_equipamento  = cd_equipamento_p;

if (qt_equipamento_w > qt_equip_agenda_w) then
	ie_disponivel_w	:= 'S';
else
	ie_disponivel_w	:= 'N';
end if;

return	ie_disponivel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
