CREATE OR REPLACE FUNCTION obter_disp_pac_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := '';
ds_dispositivo_w	varchar(255);


C01 CURSOR FOR
	SELECT	distinct
		ds_dispositivo
	from	dispositivo b,
		atend_pac_dispositivo a
	where	nr_atendimento = nr_atendimento_p
	and	a.nr_seq_dispositivo = b.nr_sequencia
	and 	ie_destacar_gpfc = 'S'
	order by 1;

BEGIN

open C01;
loop
fetch C01 into
	ds_dispositivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_dispositivo_w;
	else
		ds_retorno_w := ds_retorno_w||','||ds_dispositivo_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
