SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_divergencia_sem_justif ( nr_seq_nf_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ie_exige_w		varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	nota_fiscal_item b
where	b.nr_sequencia = nr_seq_nf_p
and	b.nr_item_nf = nr_seq_item_p
and exists (
	SELECT	1
	from	divergencia_ordem_compra_item a
	where	a.nr_seq_nf = b.nr_sequencia
	and	a.nr_item_nf = b.nr_item_nf);

if (qt_existe_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	nota_fiscal_item b
	where	b.nr_sequencia = nr_seq_nf_p
	and	b.nr_item_nf = nr_seq_item_p
	and	coalesce(b.ds_justificativa::text, '') = '';

	if (qt_existe_w > 0) then
		ie_exige_w	:= 'S';

		select	count(*)
		into STRICT	qt_existe_w
		from	divergencia_ordem_compra_item a,
			divergencia_oci_justif b
		where	a.nr_sequencia = b.nr_seq_oci_just
		and	a.nr_seq_nf = nr_seq_nf_p
		and	a.nr_item_nf = nr_seq_item_p;

		if (qt_existe_w > 0) then
			select	count(*)
			into STRICT	qt_existe_w
			from	divergencia_ordem_compra_item a
			where	a.nr_seq_nf = nr_seq_nf_p
			and	a.nr_item_nf = nr_seq_item_p
			and (exists (
				SELECT	1
				from	divergencia_oci_justif b
				where	a.nr_sequencia = b.nr_seq_oci_just
				and (coalesce(b.ds_justificativa::text, '') = '' and coalesce(b.nr_seq_tipo_justificativa::text, '') = ''))
			or not exists (
			   	SELECT	1
				from	divergencia_oci_justif b
				where	a.nr_sequencia = b.nr_seq_oci_just));

			if (qt_existe_w = 0) then
				ie_exige_w	:= 'N';
			end if;
		else
			ie_exige_w := 'S';
		end if;
	end if;

	if (ie_exige_w = 'N') then

		select	count(*)
		into STRICT	qt_existe_w
		from	divergencia_ordem_compra_item a
		where	a.nr_seq_nf = nr_seq_nf_p
		and	a.nr_item_nf = nr_seq_item_p
		and	a.ie_permite_justif = 'N';

		if (qt_existe_w > 0) then
			ie_exige_w := 'S';
		end if;
	end if;
	end;
end if;

return	ie_exige_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_divergencia_sem_justif ( nr_seq_nf_p bigint, nr_seq_item_p bigint) FROM PUBLIC;

