CREATE OR REPLACE FUNCTION obter_doacao_impedimento ( NR_SEQ_DOACAO_P bigint, IE_CODIGO_DESCRICAO_P text) RETURNS varchar AS $body$
DECLARE


DS_RESULTADO_W			varchar(2000) := '';
ds_imp_w				varchar(2000);

C01 CURSOR FOR
	SELECT 	CASE WHEN upper(substr(ie_codigo_descricao_p,1,1))='C' THEN to_char(a.nr_seq_impedimento) || ','  ELSE b.ds_impedimento || chr(13) || chr(10) END
	from	san_impedimento b,
		san_doacao_impedimento a
	where 	a.nr_seq_impedimento = b.nr_sequencia
	and 	a.nr_seq_doacao = nr_seq_doacao_p
	
union

	SELECT CASE WHEN upper(substr(ie_codigo_descricao_p,1,1))='C' THEN to_char(a.nr_seq_impedimento) || ','  ELSE b.ds_impedimento || chr(13) || chr(10) END 
	from	san_impedimento b,
		san_questionario a
	where 	a.nr_seq_impedimento 	= b.nr_sequencia
	and 	a.nr_seq_doacao 	= nr_seq_doacao_p
	and    coalesce(a.ie_impede_doacao,'N') = 'S'
	order by 1;


BEGIN

OPEN C01;
LOOP
FETCH C01 into ds_imp_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

ds_resultado_w := ds_resultado_w || ds_imp_w;

END LOOP;
CLOSE C01;

RETURN substr(DS_RESULTADO_W,1,length(DS_RESULTADO_W) - 1);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
