CREATE OR REPLACE FUNCTION obter_documento_externo_cot (nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


nr_documento_externo_w	cot_compra.nr_documento_externo%type;


BEGIN

select	max(nr_documento_externo)
into STRICT	nr_documento_externo_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (coalesce(nr_documento_externo_w::text, '') = '') then
	select	max(a.nr_documento_externo)
	into STRICT	nr_documento_externo_w
	from	ordem_compra a,
		ordem_compra_item b
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	b.nr_cot_compra = nr_cot_compra_p;
end if;

return	nr_documento_externo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
