CREATE OR REPLACE FUNCTION obter_doc_guias_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


nr_doc_conv_w		varchar(255);
nr_doc_conv_atual_w	varchar(20);

c01 CURSOR FOR
	SELECT		a.nr_doc_convenio
	from		valores_atend_paciente_v a
	where		a.nr_interno_conta = nr_interno_conta_p
	and (a.quantidade <> 0 or a.valor <> 0)
	and		coalesce(a.cd_motivo_exc_conta::text, '') = ''
	group by	a.nr_doc_convenio
	order by	1;


BEGIN

open	c01;
loop
fetch	c01	into nr_doc_conv_atual_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_doc_conv_atual_w IS NOT NULL AND nr_doc_conv_atual_w::text <> '') then
		nr_doc_conv_w	:= nr_doc_conv_w || nr_doc_conv_atual_w || ' / ';
	else
		nr_doc_conv_w	:= '';
	end if;
	end;
end loop;
close c01;

RETURN	nr_doc_conv_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
