CREATE OR REPLACE FUNCTION obter_doc_interno_devolucao ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
nr_doc_interno_w		numeric(20);
nr_doc_interno_aux_w		numeric(20);
ie_tipo_doc_interno_w		varchar(15);
ie_tipo_doc_interno_aux_w	varchar(15);

 

BEGIN 
 
select	max(nr_doc_interno), 
	max(nr_doc_interno_aux), 
	max(ie_tipo_doc_interno), 
	max(ie_tipo_doc_interno_aux) 
into STRICT	nr_doc_interno_w, 
	nr_doc_interno_aux_w, 
	ie_tipo_doc_interno_w, 
	ie_tipo_doc_interno_aux_w 
from	prescr_material_devolucao 
where	nr_prescricao 		= nr_prescricao_p 
and	nr_seq_prescricao	= nr_sequencia_p;
 
if (ie_usuario_p = 'F') then 
	if (ie_opcao_p = 'D') then 
		ds_retorno_w := substr(obter_nome_pessoa_barras(nr_doc_interno_w,ie_tipo_doc_interno_w),1,255);
	else 
		ds_retorno_w := to_char(nr_doc_interno_w);
	end if;
else 
	if (ie_opcao_p = 'D') then 
		ds_retorno_w := substr(obter_nome_pessoa_barras(nr_doc_interno_aux_w,ie_tipo_doc_interno_aux_w),1,255);
	else 
		ds_retorno_w := to_char(nr_doc_interno_aux_w);
	end if;	
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
