SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_doc_possui_material (cd_material_p bigint, ie_tipo_p text, nr_documento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_itens_w			smallint;


BEGIN

ds_retorno_w := 'N';

if (ie_tipo_p = 'O') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	ordem_compra_item
	where	nr_ordem_compra = nr_documento_p
	and		cd_material = cd_material_p;
elsif (ie_tipo_p = 'S') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	solic_compra_item
	where	nr_solic_compra = nr_documento_p
	and		cd_material = cd_material_p;
elsif (ie_tipo_p = 'N') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	nota_fiscal_item
	where	nr_sequencia_nf = nr_documento_p
	and		cd_material = cd_material_p;
elsif (ie_tipo_p = 'C') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	cot_compra_item
	where	nr_cot_compra = nr_documento_p
	and		cd_material = cd_material_p;
elsif (ie_tipo_p = 'L') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	reg_lic_item
	where	nr_seq_licitacao = nr_documento_p
	and		cd_material = cd_material_p;
elsif (ie_tipo_p = 'R') then
	select 	count(*)
	into STRICT	qt_itens_w
	from 	item_requisicao_material
	where	nr_requisicao = nr_documento_p
	and		cd_material = cd_material_p;
end if;

if (qt_itens_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
