CREATE OR REPLACE FUNCTION obter_doencas_res_negativo (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_doenca_w				varchar(50);
ds_doencas_w			varchar(200);

C01 CURSOR FOR
	SELECT	substr(san_obter_desc_exame(a.nr_seq_exame),1,50)
	into STRICT	ds_doenca_w
	from	san_exame_realizado a
	where	upper(a.ds_resultado) like upper('negativo')
	and		a.nr_seq_exame_lote = (	SELECT	b.nr_sequencia
									from	san_exame_lote b,
											san_transfusao c
									where	b.nr_seq_transfusao = c.nr_sequencia
									and		c.nr_atendimento = nr_atendimento_p);


BEGIN
open C01;
loop
fetch C01 into
	ds_doenca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (ds_doenca_w IS NOT NULL AND ds_doenca_w::text <> '') then
			ds_doencas_w	:= ds_doenca_w || '; ' || ds_doencas_w;
		end if;
	end;
end loop;
close C01;

if (ds_doencas_w IS NOT NULL AND ds_doencas_w::text <> '') then
ds_doencas_w	:= substr(ds_doencas_w,1,length(ds_doencas_w)-2);
end if;

return	ds_doencas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_doencas_res_negativo (nr_atendimento_p bigint) FROM PUBLIC;

