CREATE OR REPLACE FUNCTION obter_doencas ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_doenca_w 	varchar(80);
ds_retorno_w	varchar(4000) := null;
ds_agente_w		varchar(4000) := null;

C01 CURSOR FOR
   SELECT  distinct ds_doenca ds_agente
   from    PACIENTE_ANTEC_CLINICO
   where   cd_pessoa_fisica = cd_pessoa_fisica_p
   order 	by 1;


BEGIN
open c01;
loop
fetch	c01 into
	ds_doenca_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ds_doenca_w IS NOT NULL AND ds_doenca_w::text <> '')then
	ds_retorno_w := ds_retorno_w || ds_doenca_w || ', ';
	end if;
end loop;
close C01;

if (substr((ds_retorno_w),1,length(ds_retorno_w)-2) is not null) then
	ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.';
else
	ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2);
end if;

return	substr(ds_retorno_w,1,255);

--tratamento em pontuaçao final da string e usar separador ' , ' apenas se houver informaçao.
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
