CREATE OR REPLACE FUNCTION obter_dominio_item_avaliar ( nr_seq_item_p bigint, ds_valor_p text, qt_valor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(100) := null;
nr_seq_dominio_w		bigint  := null;



BEGIN

select	cd_dominio
into STRICT		nr_seq_dominio_w
from		med_item_avaliar
where		nr_sequencia	= nr_seq_item_p;

if (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '') then
	begin
	select	substr(ds_valor_dominio,1,100)
	into STRICT		ds_resultado_w
	from		med_valor_dominio
	where	nr_seq_dominio	= nr_seq_dominio_w
	and	vl_dominio		= ds_valor_p;
	end;
else if (qt_valor_p IS NOT NULL AND qt_valor_p::text <> '') then
	begin
	select	ds_valor_dominio
	into STRICT		ds_resultado_w
	from		med_valor_dominio
	where	nr_seq_dominio	= nr_seq_dominio_w
	and	vl_dominio		= to_char(qt_valor_p);
	end;
end if;
end if;

return	ds_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
