SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dosagem_agente_grid ( nr_sequencia_p bigint, ie_origem_inf_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_dosagem_w		varchar(10);
cd_unidade_medida_w	varchar(30);
ds_retorno_w		varchar(255) := ' ';


BEGIN

if (ie_origem_inf_p = 1) then
	begin
	select	max(ie_tipo_dosagem)
	into STRICT	ie_tipo_dosagem_w
	from	cirurgia_agente_anestesico a,
		cirurgia_agente_anest_ocor b
	where	b.nr_sequencia	=	nr_sequencia_p
	and	a.nr_sequencia	=	b.nr_seq_cirur_agente
	and	coalesce(b.qt_dose_ataque::text, '') = ''
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(b.ie_situacao,'A') = 'A';
	exception
		when others then
			ds_retorno_w	:= ' ';
	end;

	if (ie_tipo_dosagem_w IS NOT NULL AND ie_tipo_dosagem_w::text <> '') then
		select	obter_valor_dominio(1580, ie_tipo_dosagem_w)
		into STRICT	ds_retorno_w
		;
	end if;
end if;


if (ie_origem_inf_p = 2) then
	begin
	select	max(cd_unid_medida_adm)
	into STRICT	cd_unidade_medida_w
	from	cirurgia_agente_anestesico a,
		cirurgia_agente_anest_ocor b
	where	b.nr_sequencia	=	nr_sequencia_p
	and	a.nr_sequencia	=	b.nr_seq_cirur_agente
	and	ie_modo_adm	<>	'CO'
	and	coalesce(b.qt_dose_ataque::text, '') = ''
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(b.ie_situacao,'A') = 'A';
	exception
		when others then
			ds_retorno_w	:= ' ';
	end;

	if (cd_unidade_medida_w IS NOT NULL AND cd_unidade_medida_w::text <> '') then
		select	Obter_Desc_Unid_Med(cd_unidade_medida_w)
		into STRICT	ds_retorno_w
		;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dosagem_agente_grid ( nr_sequencia_p bigint, ie_origem_inf_p bigint) FROM PUBLIC;

