SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_doses_adm_medic (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dose_adm_w		varchar(1000);
ds_dose_w		varchar(50);

c01 CURSOR FOR
SELECT	to_char(qt_dose_adm)
from	prescr_mat_alteracao
where	nr_prescricao		= nr_prescricao_p
and	nr_seq_prescricao	= nr_seq_material_p
and	ie_alteracao		= 3
order by dt_horario;


BEGIN

open C01;
loop
fetch C01 into
	ds_dose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_dose_adm_w::text, '') = '') then
		ds_dose_adm_w	:= ds_dose_w;
	else
		ds_dose_adm_w	:= ds_dose_adm_w||'-'||ds_dose_w;
	end if;
	end;
end loop;
close C01;

return	ds_dose_adm_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
