CREATE OR REPLACE FUNCTION obter_dose_espec_ataque_vanco ( nm_usuario_p text, nr_prescricao_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text, qt_peso_p bigint, qt_clearance_p bigint, ie_dialisado_p text, cd_material_p bigint, cd_unid_med_dose_p text) RETURNS bigint AS $body$
DECLARE


qt_dose_w				double precision;
qt_clearance_w			double precision;
qt_peso_w				double precision;
ie_dialisado_w			varchar(1);
cd_unid_med_dose_w		varchar(50);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w	timestamp;
ie_prim_vez_prescr_w	varchar(1) := 'S';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(dt_inicio_prescr),
			coalesce(max(dt_validade_prescr), clock_timestamp()),
			max(qt_clearence),
			coalesce(max(qt_peso), obter_peso_pf(max(cd_pessoa_fisica))),
			max(cd_pessoa_fisica),
			coalesce(max(nr_atendimento), nr_atendimento_p)
	into STRICT	dt_inicio_prescr_w,
			dt_validade_prescr_w,
			qt_clearance_w,
			qt_peso_w,
			cd_pessoa_fisica_w,
			nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	ie_prim_vez_prescr_w	:= Obter_se_mat_prim_vez_prescr(nr_atendimento_w, cd_material_p, 'E', 1);
end if;

if (ie_prim_vez_prescr_w = 'S') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (coalesce(cd_unid_med_dose_p::text, '') = '') then
		cd_unid_med_dose_w	:= obter_dados_material(cd_material_p, 'UMC');
	end if;

	cd_unid_med_dose_w			:= coalesce(cd_unid_med_dose_p, cd_unid_med_dose_w);
	qt_clearance_w				:= coalesce(qt_clearance_p, qt_clearance_w);
	qt_peso_w					:= coalesce(qt_peso_p, qt_peso_w);
	ie_dialisado_w				:= coalesce(ie_dialisado_p, ie_dialisado_w);
	nr_atendimento_w			:= coalesce(nr_atendimento_p, nr_atendimento_w);
	cd_pessoa_fisica_w			:= coalesce(cd_pessoa_fisica_p, cd_pessoa_fisica_w);

	select	coalesce(max('S'), 'N')
	into STRICT	ie_dialisado_w
	from	prescr_medica
	where	coalesce(ie_paciente_dialise, 'N') <> 'N'
	and		nr_prescricao = nr_prescricao_p;

	if (cd_unid_med_dose_w IS NOT NULL AND cd_unid_med_dose_w::text <> '') and (ie_dialisado_w IS NOT NULL AND ie_dialisado_w::text <> '') and (qt_peso_w IS NOT NULL AND qt_peso_w::text <> '') and (qt_clearance_w IS NOT NULL AND qt_clearance_w::text <> '') then

		select	max(qt_dose)
		into STRICT	qt_dose_w
		from	regra_dose_esp_vanco
		where	cd_unidade_medida = cd_unid_med_dose_w
		and		ie_paciente_dialise in (ie_dialisado_w, 'A')
		and		qt_peso_w between qt_peso_min and qt_peso_max
		and		qt_clearance_w between qt_clearance_min and qt_clearance_max;
	end if;

end if;

return	qt_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
