CREATE OR REPLACE FUNCTION obter_dose_medic_pessoa_dia ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, cd_material_p bigint, qt_dose_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w		bigint;
cd_material_w			bigint;
cd_setor_w			bigint;
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w		timestamp;
qt_dose_w			double precision;
cd_unidade_medida_dose_w	varchar(30);
qt_dose_acumulada_w		double precision;
cd_pessoa_fisica_w		varchar(10);

c01 CURSOR FOR
SELECT	 coalesce(sum(a.qt_dose),0),
	 a.cd_unid_med_dose
from	 paciente_atend_medic a,
	 paciente_atendimento b,
	 paciente_setor c
where	 a.nr_seq_atendimento   = nr_seq_atendimento_p
and	 a.nr_seq_material      = nr_seq_material_p
and	 a.cd_material 	      	= cd_material_p
and	 a.nr_seq_atendimento 	= b.nr_seq_atendimento
and	 coalesce(b.nr_prescricao::text, '') = ''
and	 b.nr_seq_paciente  	= c.nr_seq_paciente
and	 c.cd_pessoa_fisica 	= cd_pessoa_fisica_w
group by a.qt_dose,a.cd_unid_med_dose;


BEGIN

select	max(c.cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	paciente_atend_medic a,
	paciente_atendimento b,
	paciente_setor c
where	a.nr_seq_atendimento = b.nr_seq_atendimento
and	b.nr_seq_paciente = c.nr_seq_paciente
and	b.nr_seq_atendimento = nr_seq_atendimento_p;



qt_dose_acumulada_w	:= 0;
open C01;
loop
fetch C01 into
	qt_dose_w,
	cd_unidade_medida_dose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_dose_acumulada_w	:= qt_dose_acumulada_w + obter_conversao_unid_med_cons(cd_material_p,cd_unidade_medida_dose_w,qt_dose_w);
	end;
end loop;
close C01;


return qt_dose_acumulada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
