SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dose_npt_atend_dia ( nr_prescricao_p bigint, nr_seq_nut_ele_mat_p bigint, qt_dose_p bigint, ie_utiliza_horarios_p text) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w			bigint;
cd_material_w				bigint;
cd_setor_w					bigint;
dt_inicio_prescr_w			timestamp;
dt_validade_prescr_w		timestamp;
qt_dose_w					double precision;
cd_unidade_medida_dose_w	varchar(30);
ie_consiste_dose_rep_w		varchar(30);
qt_dose_acumulada_w			double precision;

c00 CURSOR FOR
SELECT	c.cd_material  --- NPT Adulta Antiga
from	nut_paciente a,
		nut_paciente_elemento b,
		nut_pac_elem_mat c
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_nut_pac_ele
and		a.nr_prescricao = nr_prescricao_p
and		c.nr_sequencia = nr_seq_nut_ele_mat_p

union all

select	c.cd_material  --- NPT Adulta Protocolo
from	nut_pac a,
		nut_pac_elem_mat c
where	a.nr_sequencia = c.nr_seq_nut_pac
and		coalesce(a.ie_npt_adulta,'S') = 'S'
and		a.nr_prescricao = nr_prescricao_p
and		c.nr_sequencia = nr_seq_nut_ele_mat_p

union all

select	d.cd_material  --- NPT Pedi√°trica e Neonatal
from	nut_pac a,
		nut_pac_elemento b,
		nut_pac_elem_mat c,
		nut_elem_material d
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_pac_elem
and		b.nr_seq_elemento = d.nr_seq_elemento
and		d.nr_sequencia = c.nr_seq_elem_mat
and		a.nr_prescricao = nr_prescricao_p
and		c.nr_sequencia = nr_seq_nut_ele_mat_p;

c01 CURSOR FOR
SELECT	sum(c.qt_dose),
		c.cd_unidade_medida_dose
FROM prescr_mat_hor c, prescr_material b, prescr_medica a
LEFT OUTER JOIN setor_atendimento d ON (a.cd_setor_atendimento = d.cd_setor_atendimento)
WHERE a.nr_prescricao		= b.nr_prescricao and a.nr_atendimento	= nr_atendimento_w and b.cd_material		= cd_material_w and b.nr_prescricao		= c.nr_prescricao and b.nr_sequencia		= c.nr_seq_material  and ((b.nr_sequencia <> nr_seq_nut_ele_mat_p and b.nr_prescricao = nr_prescricao_p) or b.nr_prescricao <> nr_prescricao_p) and coalesce(a.dt_suspensao::text, '') = '' and coalesce(b.ie_suspenso,'N')	= 'N' and coalesce(c.dt_suspensao::text, '') = '' and ((Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S') or (coalesce(d.ie_consiste_dose_rep,'N') = 'S')) group by c.cd_unidade_medida_dose

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_pac a,
		nut_pac_elemento b,
		nut_pac_elem_mat c,
		nut_elem_material d,
		prescr_medica e
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_pac_elem
and		b.nr_seq_elemento = d.nr_seq_elemento
and		d.nr_sequencia = c.nr_seq_elem_mat
and		a.nr_prescricao = e.nr_prescricao
and		d.cd_material = cd_material_w
and		coalesce(e.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	e.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_pac a,
		nut_pac_elem_mat c,
		prescr_medica d
where	a.nr_sequencia = c.nr_seq_nut_pac
and		a.nr_prescricao = d.nr_prescricao
and		coalesce(a.ie_npt_adulta,'S') = 'S'
and		c.cd_material = cd_material_w
and		coalesce(d.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	d.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_paciente a,
		nut_paciente_elemento b,
		nut_pac_elem_mat c,
		prescr_medica d
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_nut_pac_ele
and		a.nr_prescricao = d.nr_prescricao
and		cd_material = cd_material_w
and		coalesce(d.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	d.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'));

c02 CURSOR FOR
SELECT	sum(coalesce(b.qt_dose,0) * coalesce(b.nr_ocorrencia,0)),
		b.cd_unidade_medida_dose
FROM prescr_material b, prescr_medica a
LEFT OUTER JOIN setor_atendimento d ON (a.cd_setor_atendimento = d.cd_setor_atendimento)
WHERE a.nr_prescricao		= b.nr_prescricao  and a.nr_atendimento	= nr_atendimento_w and b.cd_material		= cd_material_w and ((b.nr_sequencia <> nr_seq_nut_ele_mat_p and b.nr_prescricao = nr_prescricao_p) or b.nr_prescricao <> nr_prescricao_p) and coalesce(a.dt_suspensao::text, '') = '' and ((coalesce(a.dt_liberacao, dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, dt_liberacao_medico))::text <> '') or coalesce(d.ie_consiste_dose_rep,'N') = 'S') and coalesce(b.ie_suspenso,'N')	= 'N' group by b.cd_unidade_medida_dose

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_pac a,
		nut_pac_elemento b,
		nut_pac_elem_mat c,
		nut_elem_material d,
		prescr_medica e
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_pac_elem
and		b.nr_seq_elemento = d.nr_seq_elemento
and		d.nr_sequencia = c.nr_seq_elem_mat
and		a.nr_prescricao = e.nr_prescricao
and		d.cd_material = cd_material_w
and		coalesce(e.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	e.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_pac a,
		nut_pac_elem_mat c,
		prescr_medica d
where	a.nr_sequencia = c.nr_seq_nut_pac
and		a.nr_prescricao = d.nr_prescricao
and		coalesce(a.ie_npt_adulta,'S') = 'S'
and		c.cd_material = cd_material_w
and		coalesce(d.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	d.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))

union all

select	sum(coalesce(c.qt_dose,c.qt_volume)),
		coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'))
from	nut_paciente a,
		nut_paciente_elemento b,
		nut_pac_elem_mat c,
		prescr_medica d
where	a.nr_sequencia = b.nr_seq_nut_pac
and		b.nr_sequencia = c.nr_seq_nut_pac_ele
and		a.nr_prescricao = d.nr_prescricao
and		cd_material = cd_material_w
and		coalesce(d.dt_suspensao::text, '') = ''
and 	((c.nr_sequencia <> nr_seq_nut_ele_mat_p and a.nr_prescricao = nr_prescricao_p) or a.nr_prescricao <> nr_prescricao_p)
and 	d.nr_atendimento = nr_atendimento_w
group by coalesce(c.cd_unidade_medida,obter_unid_med_usua('ml'));


BEGIN

open c00;
loop
fetch c00 into cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c00 */
	begin
	cd_material_w := cd_material_w;
	end;
end loop;
close c00;

select	a.nr_atendimento,
		a.dt_inicio_prescr,
		a.dt_validade_prescr,
		a.cd_setor_atendimento
into STRICT	nr_atendimento_w,
		dt_inicio_prescr_w,
		dt_validade_prescr_w,
		cd_setor_w
from	prescr_medica a
where	a.nr_prescricao	= nr_prescricao_p;

select	coalesce(max(ie_consiste_dose_rep),'N')
into STRICT	ie_consiste_dose_rep_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_w;

if (ie_utiliza_horarios_p	= 'S') then

	qt_dose_acumulada_w	:= 0;
	open C01;
	loop
	fetch C01 into
		qt_dose_w,
		cd_unidade_medida_dose_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		qt_dose_acumulada_w	:= qt_dose_acumulada_w + obter_conversao_unid_med_cons(cd_material_w,cd_unidade_medida_dose_w,qt_dose_w);
		end;
	end loop;
	close C01;
else
	qt_dose_acumulada_w	:= 0;
	open C02;
	loop
	fetch C02 into
		qt_dose_w,
		cd_unidade_medida_dose_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		qt_dose_acumulada_w	:= qt_dose_acumulada_w + obter_conversao_unid_med_cons(cd_material_w,cd_unidade_medida_dose_w,qt_dose_w);
		end;
	end loop;
	close C02;
end if;

return qt_dose_acumulada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dose_npt_atend_dia ( nr_prescricao_p bigint, nr_seq_nut_ele_mat_p bigint, qt_dose_p bigint, ie_utiliza_horarios_p text) FROM PUBLIC;

