CREATE OR REPLACE FUNCTION obter_dose_qt_sem_apre ( cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_p text, nr_seq_ordem_p bigint, nr_seq_lote_fornec_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_cabine_w		bigint;
qt_dose_w		double precision;
qt_estoque_w		double precision;
qt_dose_real_w		double precision;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

nr_seq_ficha_tecnica_w		bigint;
cd_unidade_medida_consumo_w	varchar(30);
qt_dose_convertida_w		double precision;



BEGIN

select	b.nr_seq_cabine
into STRICT	nr_seq_cabine_w
from	far_etapa_producao b,
	can_ordem_prod a
where	a.nr_sequencia = nr_seq_ordem_p
and	b.nr_sequencia = a.nr_seq_etapa_prod;

select	CASE WHEN upper(cd_unidade_medida_p)=upper(substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30)) THEN 			qt_dose_p  ELSE qt_dose_p * obter_conversao_unid_med(cd_material_p,upper(substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30))) END
into STRICT	qt_dose_w
from	material
where	cd_material = cd_material_p;

if (nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') then
	select	coalesce(sum(qt_estoque),0)
	into STRICT	qt_estoque_w
	from	far_estoque_cabine
	where	nr_seq_cabine	   = nr_seq_cabine_w
	and 	nr_seq_lote_fornec = nr_seq_lote_fornec_p;
else
	select	coalesce(sum(qt_estoque),0)
	into STRICT	qt_estoque_w
	from	far_estoque_cabine
	where	nr_seq_cabine	   = nr_seq_cabine_w
	and	cd_material	= cd_material_p;
end if;

/*
select	nvl(sum(qt_dose_real),0)
into	qt_dose_real_w
from	can_ordem_prod_mat
where	nr_seq_ordem	= nr_seq_ordem_p
and	cd_material	= cd_material_p;
*/
select	max(nr_seq_ficha_tecnica),
		max(cd_unidade_medida_consumo)
into STRICT	nr_seq_ficha_tecnica_w,
		cd_unidade_medida_consumo_w
from	material
where	cd_material = cd_material_p;

select	sum(obter_dose_convertida_quimio(a.cd_material, a.qt_dose_real, a.cd_unidade_medida_real, cd_unidade_medida_p))
into STRICT	qt_dose_convertida_w
from	material b,
		can_ordem_prod_mat a
where	a.cd_material = b.cd_material
and		b.nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w
and		a.nr_seq_ordem	= nr_seq_ordem_p;

qt_dose_real_w	:= coalesce(obter_dose_convertida_quimio(cd_material_p, qt_dose_convertida_w, cd_unidade_medida_p, cd_unidade_medida_consumo_w),0);

qt_dose_w	:= qt_dose_w - qt_dose_real_w;

if (qt_dose_w > qt_estoque_w) then
	qt_dose_w	:= qt_estoque_w;
end if;

if (qt_dose_w < 0) then
	qt_dose_w	:= 0;
end if;

return	qt_dose_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
