CREATE OR REPLACE FUNCTION obter_dose_regra_protocolo (cd_protocolo_p bigint, nr_seq_material_p bigint, nr_seq_medicacao_p bigint, qt_idade_p bigint, qt_peso_p bigint default null) RETURNS bigint AS $body$
DECLARE

qt_dose_w	double precision;

C01 CURSOR FOR
	SELECT	qt_dose
	from	protocolo_medic_mat_regra
	where	cd_protocolo 		= cd_protocolo_p
	and		nr_seq_material 	= nr_seq_material_p
	and		nr_seq_medicacao 	= nr_seq_medicacao_p
	and		qt_idade_p between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,9999)
	and		qt_peso_p between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,9999)
	order by coalesce(qt_idade_min,0),
			coalesce(qt_idade_max,0);


BEGIN

open C01;
loop
fetch C01 into
	qt_dose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	qt_dose_w;

end;
$body$
