CREATE OR REPLACE FUNCTION obter_dose_terap_prescr_md ( ie_dose_p text, ie_fracao_dose_p text, qt_peso_p bigint, qt_dose_out_p bigint ) RETURNS bigint AS $body$
DECLARE


    qt_dose_out_W       double precision := qt_dose_out_p;


BEGIN

	if (coalesce(ie_dose_p,'F') <> 'F') then
		if (ie_fracao_dose_p = 'S') then
			qt_dose_out_W	:= qt_dose_out_W * coalesce(qt_peso_p,1);
		else
			qt_dose_out_W	:= round(qt_dose_out_W * coalesce(qt_peso_p,1));
		end if;
	end if;


    RETURN qt_dose_out_W;

END;
