CREATE OR REPLACE FUNCTION obter_dose_total_medic_pf_onc (cd_pessoa_fisica_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_paciente_w		paciente_setor.nr_seq_paciente%type;
qt_dose_total_w			double precision := 0;
qt_dose_prot_w          double precision := 0;

c01 CURSOR FOR
	SELECT nr_seq_paciente
	from paciente_setor
	where cd_pessoa_fisica = cd_pessoa_fisica_p
	order by nr_seq_paciente desc;


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
		begin

            open c01;
            loop
            fetch c01 into
                nr_seq_paciente_w;
            EXIT WHEN NOT FOUND; /* apply on c01 */
                begin

                select sum(a.qt_dose_prescricao)
                into STRICT qt_dose_prot_w
                from paciente_atend_medic a,
                     paciente_atendimento b
                where a.nr_seq_atendimento = b.nr_seq_atendimento
                and b.nr_seq_paciente = nr_seq_paciente_w
                and a.cd_material = cd_material_p
                and (b.nr_prescricao IS NOT NULL AND b.nr_prescricao::text <> '')
                and coalesce(b.dt_cancelamento::text, '') = ''
                and coalesce(b.dt_suspensao::text, '') = ''
                and exists (SELECT 1 
                            from prescr_medica 
                            where nr_prescricao = b.nr_prescricao 
                            and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
                            and coalesce(dt_suspensao::text, '') = '');

                    qt_dose_total_w := qt_dose_total_w + coalesce(qt_dose_prot_w,0);
                end;
            end loop;
            close c01;
            return qt_dose_total_w;

		end;
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
