CREATE OR REPLACE FUNCTION obter_dose_total (nr_seq_material_p bigint, nr_seq_atendimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

qt_total_w	double precision;
qt_dias_w	double precision;
qt_dose_prescricao_w	double precision;

BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

	select 	max(coalesce(qt_dias_util,1) * qt_dose_prescricao),
			max(coalesce(qt_dias_util,1)),
			max(qt_dose_prescricao)
	into STRICT 	qt_total_w,
			qt_dias_w,
			qt_dose_prescricao_w
	from	paciente_atend_medic
	where 	nr_seq_atendimento = nr_seq_atendimento_p
	and	nr_seq_material   = nr_seq_material_p;
end if;
if (ie_opcao_p = 'T') then
	return	qt_total_w;
elsif (ie_opcao_p = 'DI') then
	return	qt_dias_w;
elsif (ie_opcao_p = 'DP') then
	return qt_dose_prescricao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
