CREATE OR REPLACE FUNCTION obter_dshorarios_dt_ref (nr_sequencia_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000) := '';


BEGIN

	if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') and (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '')) then
	  select max(coalesce(b.ds_horarios, a.ds_horarios))
	    into STRICT ds_retorno_w
	    from cpoe_material a,
	         prescr_material b,
	         prescr_medica c
	   where a.nr_sequencia = b.nr_seq_mat_cpoe
	     and b.nr_prescricao = c.nr_prescricao
	     and a.nr_sequencia = nr_sequencia_p
	     and (obter_se_prescr_vig_adep(c.dt_inicio_prescr, c.dt_validade_prescr, dt_inicio_p, dt_fim_p) = 'S');
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
