CREATE OR REPLACE FUNCTION obter_ds_agenda_cons_exames ( cd_agenda_p bigint, cd_tipo_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(255);


BEGIN 
 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then 
 
	begin 
	 
	if (cd_tipo_agenda_p = 3) then 
	 
		select 	max(substr(obter_nome_medico_combo_agcons(b.cd_estabelecimento, b.cd_agenda, b.cd_tipo_agenda, 'N'),1,240)) 
		into STRICT	ds_retorno_w 
		from  	agenda_consulta a, 
			agenda b 
		where 	a.cd_agenda = b.cd_agenda 
		and	b.cd_agenda = cd_agenda_p 
		and	b.ie_situacao = 'A' 
		and	b.ie_enviar_email_confirmacao = 'S';
		 
	elsif (cd_tipo_agenda_p = 2) then 
 
		select max(CASE WHEN coalesce(b.ie_desc_curta_exame,'N')='S' THEN  coalesce(b.ds_curta,b.ds_agenda)  ELSE b.ds_agenda END ) 
		into STRICT	ds_retorno_w 
		from 	agenda_paciente a, 
			agenda b 
		where 	a.cd_agenda = b.cd_agenda 
		and	b.cd_agenda = cd_agenda_p 
		and	b.ie_situacao = 'A' 
		and	b.ie_enviar_email_confirmacao = 'S';
	 
	end if;
	 
	end;
	 
end if;	
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
