CREATE OR REPLACE FUNCTION obter_ds_classif_benef (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_seg_w	bigint;

/* Obtém a classificação do Beneficiário utilizando como parâmetro o nr_atendimento*/

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select coalesce(max(nr_seq_segurado),0)
	into STRICT nr_seq_seg_w
	from atendimento_paciente
	where nr_atendimento = nr_atendimento_p;
	if (nr_seq_seg_w > 0) then
		select (substr(pls_obter_desc_classif_benef(pls_obter_dados_segurado(nr_seq_seg_w,'CLAS')),1,255))
		into STRICT ds_retorno_w
		;
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
