CREATE OR REPLACE FUNCTION obter_ds_cod_prof (CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE


DS_RESULTADO_W			varchar(100);


BEGIN

IF (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') THEN
	SELECT	B.SG_CONSELHO ||' '||A.DS_CODIGO_PROF
	INTO STRICT	DS_RESULTADO_W
	FROM pessoa_fisica a
LEFT OUTER JOIN conselho_profissional b ON (A.NR_SEQ_CONSELHO = B.NR_SEQUENCIA)
WHERE A.CD_PESSOA_FISICA = CD_PESSOA_FISICA_P;
END IF;

RETURN DS_RESULTADO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
