CREATE OR REPLACE FUNCTION obter_ds_condicao_pagamento (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(80);


BEGIN

select	ds_condicao_pagamento
into STRICT	retorno_w
from	fis_tributo_presumido a,
	condicao_pagamento b
where	b.cd_condicao_pagamento = a.cd_condicao_pagamento
and	a.nr_sequencia = nr_sequencia_p;

return	retorno_w;

end;
$body$
