CREATE OR REPLACE FUNCTION obter_ds_descricao_setor (cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w		varchar(100);


BEGIN

select	coalesce(ds_descricao, substr(obter_desc_expressao(cd_exp_setor_atend,ds_setor_atendimento),1,100))
