CREATE OR REPLACE FUNCTION obter_ds_email_destino_agexame ( nr_sequencia_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_email_w	varchar(255);
cd_convenio_w	integer;

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select	cd_convenio
	into STRICT	cd_convenio_w
	from	agenda_paciente
	where	nr_sequencia = nr_sequencia_p;

	select	ds_email_autor
	into STRICT	ds_email_w
	from	tiss_parametros_convenio
	where	cd_convenio 		= cd_convenio_w
	and	cd_estabelecimento	= cd_estabelecimento_p;

	if (coalesce(ds_email_w::text, '') = '') or (ds_email_w = '') then
		begin
		select	c.ds_email
		into STRICT	ds_email_w
		from	pessoa_juridica_estab c,
			pessoa_juridica a,
			convenio b
		where	b.cd_convenio		= cd_convenio_w
		and	a.cd_cgc		= b.cd_cgc
		and	a.cd_cgc		= c.cd_cgc
		and	c.cd_estabelecimento	= cd_estabelecimento_p;

		if (coalesce(ds_email_w::text, '') = '') or (ds_email_w = '') then
			begin
			select	coalesce(a.ds_email,'')
			into STRICT	ds_email_w
			from	pessoa_juridica a,
				convenio b
			where	b.cd_convenio	= cd_convenio_w
			and	a.cd_cgc	= b.cd_cgc;
			end;
		end if;
		end;
	end if;
	end;
end if;
return ds_email_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
