CREATE OR REPLACE FUNCTION obter_ds_exame_prescricao ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint ) RETURNS varchar AS $body$
DECLARE

    ds_exame_w varchar(255);

BEGIN

    SELECT SUBSTR(coalesce(obter_desc_proc_interno(prescr_proc.nr_seq_proc_interno), obter_desc_procedimento(prescr_proc.cd_procedimento, prescr_proc.ie_origem_proced)), 1, 255) ds_exame
    INTO STRICT ds_exame_w
    FROM prescr_procedimento prescr_proc
    WHERE prescr_proc.nr_prescricao         = nr_prescricao_p
    AND prescr_proc.nr_sequencia = nr_sequencia_prescricao_p;

    RETURN ds_exame_w;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
