CREATE OR REPLACE FUNCTION obter_ds_formatada_regulatorio ( ds_texto_p text, ie_tipo_revisao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);

/*
Utilizada para formatar descrições a serem apresentadas em relatórios

ie_tipo_revisao_p
I	= Incluido
A	= Alterado
E	= Excluído
*/
BEGIN

if ie_tipo_revisao_p = 'I' then
	ds_retorno_w	:= Obter_Desc_Formatada(ds_texto_p,'NI');
elsif ie_tipo_revisao_p = 'A' then
	ds_retorno_w	:= Obter_Desc_Formatada(ds_texto_p,'N');
elsif ie_tipo_revisao_p = 'E' then
	ds_retorno_w	:= Obter_Desc_Formatada(ds_texto_p,'K');
else
  ds_retorno_w := ds_texto_p;
end if;

RETURN trim(both ds_retorno_w);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
