SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ds_gerencia_resp (cd_responsavel_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_gerencia_w	varchar(255);

/*
OS 1745975
Objetivo de uso: Retornar a descrição da(s) gerência(s) que possuem a PF recebida por parâmetro como responsável.
Function utilizada no relatório "Training Performance Summary - Third Party", desenvolvido para o controle do KPI de treinamentos concluídos
*/
c01 CURSOR FOR
	SELECT	ds_gerencia
	from	gerencia_wheb
	where 	cd_responsavel = cd_responsavel_p
	order by 	ds_gerencia;
c01_w	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(c01_w.ds_gerencia,'X') <> 'X') then
		ds_gerencia_w := substr(ds_gerencia_w || c01_w.ds_gerencia || ', ',1,255);
	end if;

end loop;
close c01;

ds_retorno_w := substr(ds_gerencia_w,1,length(ds_gerencia_w)-2);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
