CREATE OR REPLACE FUNCTION obter_ds_idade_igc (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w varchar(50);


BEGIN 
 
select	substr(obter_idade_igc(clock_timestamp(), b.dt_nascimento, b.dt_nascimento_ig, b.qt_dias_ig, b.qt_semanas_ig),1,50) 
into STRICT	ds_retorno_w 
from	atendimento_paciente a, 
		pessoa_fisica b 
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and		a.nr_atendimento = nr_atendimento_p;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
