CREATE OR REPLACE FUNCTION obter_ds_indicador_graf ( nr_seq_indicador_p bigint, nr_painel_controle_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_indicador_p IS NOT NULL AND nr_seq_indicador_p::text <> '') then

	select	ds_indicador
	into STRICT	ds_retorno_w
	from	regra_indicador_visual
	where	nr_sequencia = nr_seq_indicador_p;

elsif (nr_painel_controle_p IS NOT NULL AND nr_painel_controle_p::text <> '') then

	select	ds_indicador
	into STRICT	ds_retorno_w
	from	regra_painel_controle
	where	nr_sequencia = nr_painel_controle_p;

end if;

return	ds_retorno_w;

end;
$body$
