CREATE OR REPLACE FUNCTION obter_ds_item_inst_pepo (nr_seq_obj_sch_p bigint) RETURNS varchar AS $body$
DECLARE

ds_item_w			varchar(255);
ds_item_perfil_w	varchar(255);
ds_retorno_w		varchar(255);
nr_item_pepo_w		bigint;

BEGIN
if (nr_seq_obj_sch_p <> 122272) then	
	select 	substr(coalesce(a.ds_item_instituicao,obter_desc_expressao(a.cd_exp_desc_item,a.ds_item)), 0, 255),
			a.nr_sequencia
	into STRICT	ds_item_w,
			nr_item_pepo_w
	from   	pepo_item a
	where  	coalesce(a.nr_seq_superior::text, '') = ''	
	and (a.ie_situacao_html5 = 'A' or coalesce(a.ie_situacao_html5::text, '') = '')
	and (OBTER_ITEM_ARVORE_PEPO(a.nr_sequencia, null) = nr_seq_obj_sch_p);			
	select 	substr(max(b.ds_item_instituicao), 0, 255)
	into STRICT	ds_item_perfil_w
	from   	perfil_item_pepo b
	where  	b.cd_perfil = wheb_usuario_pck.get_cd_perfil
	and    	b.nr_seq_item_pepo not in (110, 106, 69)
	and 	b.nr_seq_item_pepo = nr_item_pepo_w;
	if (ds_item_perfil_w IS NOT NULL AND ds_item_perfil_w::text <> '') then
		ds_retorno_w := ds_item_perfil_w;
	else
		ds_retorno_w := ds_item_w;
	end if;
else
	select substr(obter_desc_expressao(cd_exp_desc_obj,null), 0, 255) ds_item_pepo
	into STRICT 	ds_retorno_w
	from 	objeto_schematic
	where 	nr_Sequencia = nr_seq_obj_sch_p;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
