CREATE OR REPLACE FUNCTION obter_ds_local_senha ( ds_maquina_chamada_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);


BEGIN

	SELECT	ds_local
	into STRICT 	ds_retorno_w
	FROM	maquina_local_senha a,
			computador b
	WHERE	a.nr_seq_computador	= b.nr_sequencia
	AND		coalesce(a.ie_situacao, 'A') = 'A'
	AND 	nm_computador = ds_maquina_chamada_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
