CREATE OR REPLACE FUNCTION obter_ds_modulo_pais (nr_seq_modulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_modulo_w		varchar(255);
nr_seq_modulo_w  bigint;


BEGIN

if (nr_seq_modulo_p IS NOT NULL AND nr_seq_modulo_p::text <> '')then
	select	nr_seq_modulo
	into STRICT 	nr_seq_modulo_w
	from 	latam_modulo
	where 	nr_sequencia = nr_seq_modulo_p;

	if (nr_seq_modulo_w IS NOT NULL AND nr_seq_modulo_w::text <> '')then
		select 	substr(obter_descricao_padrao('LATAM_CADASTRO_MODULO', 'DS_MODULO', nr_seq_modulo_w),1,80)
		into STRICT ds_modulo_w
		;
	end if;
end if;

return	ds_modulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
