CREATE OR REPLACE FUNCTION obter_ds_nivel_plan ( nr_atendimento_p ATEND_CATEGORIA_CONVENIO.NR_ATENDIMENTO%type ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  ATEND_CATEGORIA_CONVENIO.DS_TARJA_CARTAO%type;


BEGIN

SELECT  MAX(a.DS_NIVEL)
INTO STRICT    ds_retorno_w
FROM    NIVEL_FILIACAO a, ATEND_CATEGORIA_CONVENIO b
