CREATE OR REPLACE FUNCTION obter_ds_nome_atributo ( NR_SEQUENCIA_P bigint, NM_ATRIBUTO_P text ) RETURNS varchar AS $body$
DECLARE


 DS_NOME_ATRIBUTO DIC_EXPRESSAO_IDIOMA.DS_EXPRESSAO%type;


BEGIN

  SELECT OBTER_DESC_EXPRESSAO(cd_exp_label) DS_RESULT INTO STRICT DS_NOME_ATRIBUTO
    FROM TABELA_ATRIBUTO_LINKED
  WHERE NR_SEQ_ELEMENTO = NR_SEQUENCIA_P
