CREATE OR REPLACE FUNCTION obter_ds_observacao_cpaciente (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
contador_cpo_w smallint;
ds_observacao_w varchar(255);
nr_max_seq_w	conta_paciente_observacao.nr_sequencia%type;

BEGIN
select count(*) 
into STRICT contador_cpo_w 
from  conta_paciente_observacao co 
where co.nr_interno_conta = nr_interno_conta_p 
and  (co.dt_liberacao IS NOT NULL AND co.dt_liberacao::text <> '');
	 
if (contador_cpo_w > 0) then 
 
  select max(coalesce(co.nr_sequencia,0)) 
  into STRICT  nr_max_seq_w 
  from  conta_paciente_observacao co 
  where co.nr_interno_conta = nr_interno_conta_p 
  and  (co.dt_liberacao IS NOT NULL AND co.dt_liberacao::text <> '');
   
  select substr(co.ds_observacao,1,255) 
  into STRICT  ds_observacao_w 
  from  conta_paciente_observacao co 
  where nr_sequencia = nr_max_seq_w;
  
  return ds_observacao_w;
else 
	  
  select substr(c.ds_observacao,1,255) 
  into STRICT ds_observacao_w 
  from conta_paciente c 
  where c.nr_interno_conta = nr_interno_conta_p;
  
  return ds_observacao_w;	
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ds_observacao_cpaciente (nr_interno_conta_p bigint) FROM PUBLIC;

