CREATE OR REPLACE FUNCTION obter_ds_pend_quimio (nr_seq_pend_quimio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);


BEGIN

if (nr_seq_pend_quimio_p IS NOT NULL AND nr_seq_pend_quimio_p::text <> '') then

    select	max(substr(c.nr_sequencia||' - '||obter_desc_prot_medic(a.nr_seq_paciente),1,255)) ds

    into STRICT ds_retorno_w

    from	paciente_setor a,
            paciente_atendimento b,
            qt_pendencia_agenda c

    where	a.nr_seq_paciente		= b.nr_seq_paciente
    and		b.nr_seq_pend_agenda	= c.nr_sequencia
    and		c.nr_sequencia	        = nr_seq_pend_quimio_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
