CREATE OR REPLACE FUNCTION obter_ds_retorno_par_antrop_md (ie_tipo_p text, ie_valor_igual_p bigint, ie_valor_inicio_p bigint, ie_valor_final_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(255) := '';

BEGIN

  if (ie_tipo_p <> 'IMC') then
    if (ie_valor_igual_p IS NOT NULL AND ie_valor_igual_p::text <> '') then
    
      ds_retorno_w := obter_desc_expressao(630019, 'Percentil') || ' ' ||
                      ie_valor_igual_p;

    elsif (ie_valor_inicio_p IS NOT NULL AND ie_valor_inicio_p::text <> '') and (ie_valor_final_p IS NOT NULL AND ie_valor_final_p::text <> '') then
    
      ds_retorno_w := obter_desc_expressao(630019, 'Percentil') || ' ' ||
                      obter_desc_expressao(763609, 'entre') || ' ' ||
                      ie_valor_inicio_p || ' ' ||
                      obter_desc_expressao(312342, 'e') || ' ' ||
                      ie_valor_final_p;

    elsif (coalesce(ie_valor_inicio_p::text, '') = '') and (ie_valor_final_p IS NOT NULL AND ie_valor_final_p::text <> '') then
    
      ds_retorno_w := obter_desc_expressao(630019, 'Percentil') || ' ' ||
                      lower(obter_desc_expressao(724788, 'maior que')) || ' ' ||
                      ie_valor_final_p;

    elsif (ie_valor_inicio_p IS NOT NULL AND ie_valor_inicio_p::text <> '') and (coalesce(ie_valor_final_p::text, '') = '') then
    
      ds_retorno_w := obter_desc_expressao(630019, 'Percentil') || ' ' ||
                      lower(obter_desc_expressao(498433, 'menor que')) || ' ' ||
                      ie_valor_inicio_p;

    end if;
  end if;
  return ds_retorno_w;
end;
$body$
