CREATE OR REPLACE FUNCTION obter_ds_ret_prim_horario_md ( dt_inicio_prescr_p timestamp ) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w			varchar(5);

BEGIN
    IF ( MOD(pkg_date_utils.extract_field('HOUR', dt_inicio_prescr_p), 2) = 0 ) THEN
        ds_retorno_w := to_char(trunc(dt_inicio_prescr_p, 'hh') + 2 / 24, 'hh24:mi');
    ELSE
        ds_retorno_w := to_char(trunc(dt_inicio_prescr_p, 'hh') + 1 / 24, 'hh24:mi');
    END IF;

    RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
