CREATE OR REPLACE FUNCTION obter_ds_ret_sinal_vital_md (ie_sinal_p text, ds_mensagem_bloqueio_p text, qt_minimo_per_p bigint, vl_maximo_per_p bigint, ds_sinal_vital_p text, ds_mensagem_alerta_p text, qt_min_aviso_p bigint, qt_max_aviso_p bigint, ie_decimal_p text, vl_decimal_p bigint ) RETURNS varchar AS $body$
DECLARE


   ds_retorno_w varchar(1000);

BEGIN
	--INICIO MD 3
	if (ie_sinal_p	= 'E') then
		if (ds_mensagem_bloqueio_p IS NOT NULL AND ds_mensagem_bloqueio_p::text <> '') then
			ds_retorno_w	:= ds_mensagem_bloqueio_p;
		else
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(300322, 'QT_MINIMO_PER_W=' || to_char(qt_minimo_per_p) ||
				';VL_MAXIMO_PER_W=' || to_char(vl_maximo_per_p) || ';DS_SINAL_VITAL_W=' || ds_sinal_vital_p);
		end if;
	elsif (ie_sinal_p	= 'A') then
		if (ds_mensagem_alerta_p IS NOT NULL AND ds_mensagem_alerta_p::text <> '') then
			ds_retorno_w	:= ds_mensagem_alerta_p;
		else
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(300334, 'QT_MIN_AVISO_W=' || to_char(qt_min_aviso_p) ||
				';QT_MAX_AVISO_W=' || to_char(qt_max_aviso_p) || ';DS_SINAL_VITAL_W=' || ds_sinal_vital_p);
		end if;
	end if;
	--FIM MD 3
	if (ie_decimal_p = 'N') and (vl_decimal_p IS NOT NULL AND vl_decimal_p::text <> '') then
		--RETORNO PARA MD 4
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(300335, 'DS_RETORNO_P=' || ds_retorno_w ||
			';VL_DECIMAL_W=' || vl_decimal_p), 1, 255);
	end if;		


    RETURN ds_retorno_w;

END;
