SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ds_senha_agenda ( nr_seq_senha_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
 
cd_tipo_agenda_w	bigint;

ds_senha_w		varchar(20);

ie_em_atendimento_w	varchar(1);


BEGIN 
 
 
 
select	max(coalesce(cd_tipo_agenda, 0)) 
 
into STRICT	cd_tipo_agenda_w 
 
from	agenda 
 
where	cd_agenda = cd_agenda_p;
 
 
 
ds_senha_w := '';
 
 
 
begin 
 
	select	'S' 
 
	into STRICT	ie_em_atendimento_w 
 
	from	atendimentos_senha a, 
 
		agendas_lib_fila_senha b 
 
	where	b.nr_seq_fila_espera = a.nr_seq_fila_espera 
 
	and	b.nr_seq_agenda = cd_agenda_p 
 
	and	(a.dt_inicio_atendimento IS NOT NULL AND a.dt_inicio_atendimento::text <> '') 
 
	and	coalesce(a.dt_fim_atendimento::text, '') = '' 
 
	and	a.nr_seq_pac_senha_fila = nr_seq_senha_p  LIMIT 1;
 
exception 
 
	when	no_data_found then 
 
		ie_em_atendimento_w := 'N';
 
end;
 
 
 
if (ie_em_atendimento_w = 'S') then 
 
	if (cd_tipo_agenda_w in (3,4,5)) then 
 
		 
 
		select	max(substr(obter_letra_verifacao_senha(coalesce(a.nr_seq_fila_senha_origem, a.nr_seq_fila_senha)),1,10) || cd_senha_gerada) 
 
		into STRICT	ds_senha_w 
 
		from	paciente_senha_fila a, 
 
			agenda_consulta b 
 
		where	a.nr_sequencia = nr_seq_senha_p 
 
		and	b.cd_agenda = cd_agenda_p 
 
		and	b.nr_seq_pac_senha_fila = a.nr_sequencia 
 
		and	trunc(b.dt_agenda) = trunc(clock_timestamp());
 
		 
 
	elsif (cd_tipo_agenda_w in (1,2)) then 
 
		 
 
		select	max(substr(obter_letra_verifacao_senha(coalesce(a.nr_seq_fila_senha_origem, a.nr_seq_fila_senha)),1,10) || cd_senha_gerada) 
 
		into STRICT	ds_senha_w 
 
		from	paciente_senha_fila a, 
 
			agenda_paciente b 
 
		where	a.nr_sequencia = nr_seq_senha_p 
 
		and	b.cd_agenda = cd_agenda_p 
 
		and	b.nr_seq_pac_senha_fila = a.nr_sequencia 
 
		and	trunc(b.hr_inicio) = trunc(clock_timestamp());		
 
	end if;
 
end if;
 
 
 
return	ds_senha_w;
 
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ds_senha_agenda ( nr_seq_senha_p bigint, cd_agenda_p bigint) FROM PUBLIC;

