CREATE OR REPLACE FUNCTION obter_ds_setor_prescr_proc (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_setor_atendimento_w varchar(100);
cd_setor_atendimento_w integer;


BEGIN
ds_setor_atendimento_w := '';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select	coalesce(max(a.cd_setor_atendimento),0)
	into STRICT 	cd_setor_atendimento_w
	from    prescr_procedimento a
	where	a.nr_prescricao	= nr_prescricao_p
	and		a.nr_sequencia	= nr_seq_prescr_p;

	select  coalesce(max(a.ds_setor_atendimento),'')
	into STRICT	ds_setor_atendimento_w
	from	setor_atendimento a
	where	a.cd_setor_atendimento	= cd_setor_atendimento_w;

end if;

return	ds_setor_atendimento_w;

end;
